package com.debasish.demomap.google_map;import android.annotation.SuppressLint;import android.content.Context;import android.location.Address;import com.google.android.gms.location.LocationRequest;import com.patloew.rxlocation.RxLocation;import io.reactivex.Observable;import io.reactivex.Single;public class MapManager implements IMapManager {    public static final String TAG = MapManager.class.getSimpleName();    Context context;    RxLocation rxLocation;    LocationRequest locationRequest;    public MapManager (Context context) {        this.context = context;        rxLocation= new RxLocation(context);        locationRequest = new LocationRequest();        locationRequest.setInterval(3000);        locationRequest.setFastestInterval(3000);        locationRequest.setPriority(LocationRequest.PRIORITY_HIGH_ACCURACY);    }    @Override    public Single<Boolean> checkLocationSettings () {        return rxLocation                .settings()                .checkAndHandleResolution(locationRequest);    }    @SuppressLint("MissingPermission")    @Override    public Observable<Address> getLatestLocation () {        return rxLocation                    .location()                .updates(locationRequest)                .flatMap(location -> rxLocation.geocoding().fromLocation(location).toObservable());                }}