package com.debasish.demomap.util;import android.content.Context;import android.support.annotation.NonNull;import android.support.v7.app.AppCompatActivity;import com.debasish.demomap.ui.activity.home.MainActivity;import com.google.android.gms.common.api.GoogleApiClient;import com.google.android.gms.location.FusedLocationProviderClient;import com.google.android.gms.location.LocationRequest;import com.google.android.gms.location.LocationServices;import com.google.android.gms.location.LocationSettingsRequest;import com.google.android.gms.location.LocationSettingsResponse;import com.google.android.gms.location.LocationSettingsStates;import com.google.android.gms.location.SettingsClient;import com.google.android.gms.tasks.OnCompleteListener;import com.google.android.gms.tasks.Task;public class GoogleApi {    private final LocationRequest locationRequest;    public Context context;    public GoogleApi (Context context) {        this.context = context;        locationRequest = new LocationRequest();        locationRequest.setInterval(3000);        locationRequest.setFastestInterval(3000);        locationRequest.setPriority(LocationRequest.PRIORITY_HIGH_ACCURACY);    }    public Task<LocationSettingsResponse> getLocationSettingStates(){        LocationSettingsRequest.Builder builder = new LocationSettingsRequest                .Builder()                .addLocationRequest(locationRequest).setAlwaysShow(true);        LocationSettingsRequest locationSettingsRequest=  builder.build();        SettingsClient settingsClient= LocationServices.getSettingsClient(context);        return settingsClient.checkLocationSettings(locationSettingsRequest);      }}